import R from 'ramda'
import {FETCH_PHONES_SUCCESS} from 'actionTypes'

const initialState ={};


export default (state = initialState, {type, payload})=> {
    switch(type){
        case FETCH_PHONES_SUCCESS:
            const newValues = R.indexBy(R.prop('id'), payload)//new object with prop 'id' of phones as key - our state of phones
            return R.merge(state, newValues)
        default:
            return state
    }
};

