import R from 'ramda';

const getPhoneById = (state, id)=> R.prop(id, state.phones)//


export const getPhones = state => {
    const phones = R.map(id=> {
        console.log('getPhoneById(state, id)', getPhoneById(state, id))
        return getPhoneById(state, id)
    }, state.phonesPage.ids)

    console.log('phones', phones)
    return phones
}